import React, { useState }  from 'react'
import { useSelector } from "react-redux"
import { GoogleMap, Marker, withGoogleMap, withScriptjs} from "react-google-maps"


function Gmap() {
    const [lat, setLat] = useState('1.286920')
    const [lng, setLng] = useState('103.854570')
    const allLocations = useSelector(state=>state.locations)

    const handleCLick = (lat,lng) =>{
        setLat(lat)
        setLng(lng)
      }

    //google map
    const MyMapComponent =  withScriptjs(withGoogleMap((props) =>
    <GoogleMap
        defaultZoom={15}
        defaultCenter={{ lat: Number(lat), lng: Number(lng)}}
     >
    {
        props.marker.map(marker => (
        <Marker onClick={()=>handleCLick(marker.lat, marker.long)}  position={{ lat:Number(marker.lat), lng: Number(marker.long) }}/>      
        ))
    }
    </GoogleMap>
  ))
    return (
        <div>
            <MyMapComponent
                isMarkerShown
                googleMapURL="https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDoq9UGXD1xeWQTE76a67VgbGi8p3idiBE"
                loadingElement={<div style={{ height: `100%` }} />}
                containerElement={<div style={{ height: `100vh` }} />}
                mapElement={<div style={{ height: `100%` }} />}
                marker={allLocations}
            />
        </div>
    )
}

export default Gmap
