import React, { useEffect, useState } from 'react'
import { IoCaretDown, IoCaretUp} from "react-icons/io5";
import './Menu.css'
import $, { data } from "jquery";
import api from '../../api/locations'
import { useDispatch, useSelector } from 'react-redux';
import { setLocations } from '../../redux/action';

function Menu() {
    const [isOpen, setIsOpen] = useState(true)
    const dispatch = useDispatch()
    const AllLocations = useSelector(state=>state.locations)


    $(document).ready(function () {
        $(".list").on("click", ".content", function () {
          $(".content").removeClass("selected");
          $(this).addClass("selected");
        });
      });

    //function ambil data dari json-server
    const getLocations = async()=>{
        const response = await api.get("/data")
        dispatch(setLocations(response.data)) //kirim data ke redux
    }

    useEffect(()=>{
        getLocations() //call function getlocation on render
    },[])

    return (
        <div style={{height:'100vh'}}>
            <div className='menu'>
                <div className='filter'>
                    <select>
                        <option>Filter by favorite</option>
                    </select>
                </div>
                <div className='landmark'>
                    {
                        AllLocations.map((data,index)=>(
                            <div key={index}>
                                <div className='list' key={index}><div className='content'>{data.name}{data.place?<span onClick={()=>setIsOpen(!isOpen)}>{ isOpen ? <IoCaretUp/> : <IoCaretDown/>}</span>:null}</div></div> 
                                <ul className='drop-menu'>
                                {
                                    data.place && isOpen ?
                                      data.place.map((item,index)=>(
